import { __decorate } from "tslib";
import { Component, Input } from '@angular/core';
import { DiscountModel } from '../shared/models/discount-model.model';
import { GlobalConstants } from '../shared/global-constants.model';
let ProductCardComponent = class ProductCardComponent {
    constructor(service, DiscountsService) {
        this.service = service;
        this.DiscountsService = DiscountsService;
        this.DiscountDetails = new DiscountModel();
    }
    async getDiscountFunction(DiscountId) {
        await this.DiscountsService.GetById(DiscountId).subscribe(res => {
            this.DiscountDetails = res;
        });
    }
    createImgPath(serverpath) {
        return GlobalConstants.apiURL + serverpath;
    }
    ngOnInit() {
        this.Product;
        this.getDiscountFunction(this.Product.discountId);
    }
};
__decorate([
