        this.userForm = new FormGroup({
            id: new FormControl(''),
            userName: new FormControl(''),
            gender: new FormControl(''),
            age: new FormControl(''),
            email: new FormControl(''),
            phoneNumber: new FormControl('')
        });
        this.submitted = false;
        this.updateUser = (updateFormValue) => {
            this.submitted = true;
            if (this.userForm.invalid) {
                return;
