export class ProductsingleComponent {
    constructor(route, ProductService, CartService, authService, DiscountsService, router, OrderDetailsService) {
        this.route = route;
        this.ProductService = ProductService;
        this.CartService = CartService;
        this.authService = authService;
        this.DiscountsService = DiscountsService;
        this.router = router;
        this.OrderDetailsService = OrderDetailsService;
        this.ProductDetails = new ProductModel();
        this.DiscountDetails = new DiscountModel();
        this.netProductProfit = 0;
        this.actualProductPrice = 0;
        this.submitted = false;
        this.cartForm = new FormGroup({
            quantity: new FormControl(''),
        });
    }
    async getById(id) {
        await this.ProductService.getById(id).subscribe(async (result) => {
            this.ProductDetails = result;
            await this.getDiscountFunction(result);
            await this.actualPriceFunction(result, this.DiscountDetails);
        });
    }
    createImgPath(serverpath) {
        return GlobalConstants.apiURL + serverpath;
    }
    async GetNetProductProfit() {
    }
    async getDiscountFunction(result) {
        await this.DiscountsService.GetById(result.discountId).subscribe(res => {
            this.DiscountDetails = res;
        });
    }
    async actualPriceFunction(result, productDiscount) {
        this.actualProductPrice = Number(result.price) + Number(productDiscount.value);
    }
    async onAddToCart(formValues) {
        this.submitted = true;
        if (this.cartForm.invalid) {
            return;
        }
        const formValue = Object.assign({}, formValues);
        var productDetail = new InModel();
        productDetail.In.quantity = formValue.quantity;
        productDetail.In.productId = this.ProductDetails.id;
        productDetail.In.userId = Number(localStorage.getItem('userId'));
        productDetail.In.isAvailable = true;
        this.CartService.add(productDetail).subscribe({
            next: (_) => {
                document.getElementById("success-alert").style.display = "block";
                document.getElementById("danger-alert").style.display = "none";
                document.getElementById("success-alert").innerHTML = "added successfully";
                this.router.navigate(['/cart']);
            },
            error: (err) => {
                document.getElementById("danger-alert").style.display = "block";
                document.getElementById("danger-alert").innerHTML = "something went wrong";
            }
        });
    }
    ngOnInit() {
        this.route.paramMap.subscribe(params => {
            var id = Number(params.get('id'));
            this.getById(id);
        });
    }
}
